import 'package:flutter/material.dart';

class CategoriesScreen extends StatefulWidget {
  final String? category; // ðŸ‘ˆ nullable

  const CategoriesScreen({super.key, this.category});

  @override
  State<CategoriesScreen> createState() => _CategoriesScreenState();
}

class _CategoriesScreenState extends State<CategoriesScreen> {
  final Map<String, List<String>> subCategoriesMap = {
    "Men": [
      "Shirts",
      "T-Shirts",
      "Sweaters",
      "Jackets",
      "Trousers",
      "Jeans",
      "Shorts",
    ],
    "Women": [
      "Tops",
      "Blouses",
      "Cardigans",
      "Knitwear",
      "Blazers",
      "Outerwear",
      "Pants",
      "Jeans",
      "Shorts",
      "Skirts",
      "Dresses",
    ],
    "Kids": ["T-Shirts", "Shorts", "Jeans", "Jackets", "Shoes"],
    "Shoes": ["Sneakers", "Formal Shoes", "Sandals", "Boots"],
    "Accessories": ["Bags", "Watches", "Belts", "Jewelry", "Hats"],
    "Bags": ["Tote Bags", "Laptop Bags", "Clutches", "Backpacks"],
  };

  // ðŸ”¹ Image mapping for main categories
  final Map<String, String> categoryImages = {
    "Men":
        "data:image/jpeg;base64,",
    "Women":
        "data:image/jpeg;base64,",
    "Kids":
        "data:image/jpeg;base64,",
    "Shoes":
        "data:image/jpeg;base64,",
    "Accessories":
        "data:image/jpeg;base64,",
    "Bags":
        "data:image/jpeg;base64,",
  };

  @override
  Widget build(BuildContext context) {
    // ðŸ‘‡ If no category passed â†’ show all categories
    if (widget.category == null) {
      return Scaffold(
        appBar: AppBar(
          title: const Text("All Categories"),
          centerTitle: true,
          actions: const [Icon(Icons.search), SizedBox(width: 10)],
        ),
        body: ListView(
          children: [
            // ðŸ”¹ Sale Banner
            Container(
              width: double.infinity,
              color: Colors.red,
              padding: const EdgeInsets.all(16),
              child: const Text(
                "SUMMER SALES\nUp to 50% off",
                style: TextStyle(color: Colors.white, fontSize: 16),
                textAlign: TextAlign.center,
              ),
            ),

            const SizedBox(height: 10),

            const Padding(
              padding: EdgeInsets.all(16.0),
              child: Text(
                "All Categories",
                style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
              ),
            ),

            // ðŸ”¹ Show all categories as grid with image cards
            GridView.builder(
              shrinkWrap: true,
              physics: const NeverScrollableScrollPhysics(),
              padding: const EdgeInsets.all(10),
              gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 2,
                childAspectRatio: 0.9,
                crossAxisSpacing: 10,
                mainAxisSpacing: 10,
              ),
              itemCount: subCategoriesMap.keys.length,
              itemBuilder: (context, index) {
                final catName = subCategoriesMap.keys.elementAt(index);
                final imageUrl = categoryImages[catName] ?? "";

                return GestureDetector(
                  onTap: () {
                    // Navigate to subcategories of this category
                    Navigator.push(
                      context,
                      MaterialPageRoute(
                        builder: (_) => CategoriesScreen(category: catName),
                      ),
                    );
                  },
                  child: Card(
                    elevation: 3,
                    shape: RoundedRectangleBorder(
                      borderRadius: BorderRadius.circular(12),
                    ),
                    clipBehavior: Clip.antiAlias,
                    child: Stack(
                      fit: StackFit.expand,
                      children: [
                        Image.network(imageUrl, fit: BoxFit.cover),
                        Container(
                          color: Colors.black45,
                          child: Center(
                            child: Text(
                              catName,
                              style: const TextStyle(
                                fontSize: 18,
                                fontWeight: FontWeight.bold,
                                color: Colors.white,
                              ),
                            ),
                          ),
                        ),
                      ],
                    ),
                  ),
                );
              },
            ),
          ],
        ),
      );
    }

    // ðŸ‘‡ Else: category is provided â†’ show subcategories
    final subCategories = subCategoriesMap[widget.category] ?? [];

    return Scaffold(
      appBar: AppBar(
        title: Text(widget.category!),
        centerTitle: true,
        actions: const [Icon(Icons.search), SizedBox(width: 10)],
      ),
      body: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          // ðŸ”¹ Sale Banner
          Container(
            width: double.infinity,
            color: Colors.red,
            padding: const EdgeInsets.all(16),
            child: const Text(
              "SUMMER SALES\nUp to 50% off",
              style: TextStyle(color: Colors.white, fontSize: 16),
              textAlign: TextAlign.center,
            ),
          ),

          const SizedBox(height: 10),

          // ðŸ”¹ View All Button
          Padding(
            padding: const EdgeInsets.all(16.0),
            child: ElevatedButton(
              style: ElevatedButton.styleFrom(
                backgroundColor: Colors.red,
                minimumSize: const Size(double.infinity, 45),
                shape: RoundedRectangleBorder(
                  borderRadius: BorderRadius.circular(8),
                ),
              ),
              onPressed: () {
                // TODO: Navigate to all items of this category
              },
              child: const Text(
                "VIEW ALL ITEMS",
                style: TextStyle(color: Colors.white),
              ),
            ),
          ),

          const Padding(
            padding: EdgeInsets.symmetric(horizontal: 16.0),
            child: Text(
              "Choose category",
              style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold),
            ),
          ),

          const SizedBox(height: 10),

          // ðŸ”¹ Subcategories List
          Expanded(
            child: ListView.builder(
              itemCount: subCategories.length,
              itemBuilder: (context, index) {
                return ListTile(
                  leading: const Icon(Icons.label_outline),
                  title: Text(subCategories[index]),
                  trailing: const Icon(Icons.arrow_forward_ios, size: 16),
                  onTap: () {
                    // TODO: Navigate to product list of this subcategory
                  },
                );
              },
            ),
          ),
        ],
      ),
    );
  }
}
