import 'package:flutter/material.dart';
import 'package:frehas_task/Home/product_detail.dart';
import 'package:frehas_task/favorites/favorites_manager.dart';

import 'categories_screen.dart';

class Product {
  final String name;
  final String image;
  final double price;
  bool isFavorite;

  Product({
    required this.name,
    required this.image,
    required this.price,
    this.isFavorite = false,
  });

  String get description => name;
}

class Category {
  final String name;
  final String image;

  Category({required this.name, required this.image});
}

class HomeScreen extends StatefulWidget {
  const HomeScreen({super.key});

  static var accent;

  @override
  State<HomeScreen> createState() => _HomeScreenState();
}

class _HomeScreenState extends State<HomeScreen> {
  // 🔹 Sample Products
  final List<Product> products = [
    Product(
      name: "Casual T-Shirt",
      image:
          "data:image/jpeg;base64,",
      price: 12.99,
    ),
    Product(
      name: "Denim Jacket",
      image:
          "data:image/jpeg;base64,",
      price: 34.50,
    ),
    Product(
      name: "Formal Shirt",
      image:
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSgQcoQbOrHjvpHyNIJHZYtVLJ89F8qGRwSEg&s",
      price: 22.00,
    ),
    Product(
      name: "Summer Dress",
      image:
          "data:image/jpeg;base64,",
      price: 29.99,
    ),
    Product(
      name: "Leather Jacket",
      image:
          "data:image/jpeg;base64,",
      price: 55.00,
    ),
    Product(
      name: "Jeans Pants",
      image:
          "data:image/jpeg;base64,",
      price: 25.99,
    ),
    Product(
      name: "Hoodie",
      image:
          "data:image/jpeg;base64,",
      price: 19.50,
    ),
    Product(
      name: "Sneakers",
      image:
          "https://images.pexels.com/photos/2529148/pexels-photo-2529148.jpeg",
      price: 45.00,
    ),
